import { useEffect, useState } from "react";
import { getStoreData } from "../../DB/Actions/retriveProducts";
import { Product, Stores } from "../../DB/db";

const AllProductsList = () => {
  const [allProducts, setAllProducts] = useState<Product[]>([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const products = await getStoreData<Product>(Stores.Products);
        setAllProducts(products);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };
    fetchData();
  }, []);

  return (
    <div className=" p-1">
      <div className="w-72 bg-white h-80 rounded p-2 shadow-md flex flex-col justify-between">
        <div>
          <div>
            <img
              src="https://picsum.photos/200"
              alt="p"
              className="w-[100%] object-fill h-[150px] rounded"
            />
          </div>
          <div className=" text-left p-2">
            <h5 className=" font-monospace">Computer Monitor</h5>
            <p className="font-monospace text-sm font-light">
              Supre fast Computer Monitor new arraival
            </p>
            <p className="font-monospace text-sm font-light">Price: $100 </p>
          </div>
        </div>
        <div>
          <button className=" bg-light-gray w-full rounded hover:bg-gray-400 ">
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
};

export default AllProductsList;
