import React from 'react'

type MyComponentProps = {
  children: React.ReactNode;
};

export default function MyComponent({children}:MyComponentProps) {

  // Wrap children in an array to use indexing, then map over each child.
  const modifiedChildren = React.Children.map(children, (child, index) => {
    // Check if the child is a button and only the first one should be clickable.
    if (React.isValidElement(child) && child.type === 'button') {
      // Only the first button remains enabled, others are disabled.
      return React.cloneElement(child, { disabled: index !== 0 });
    }
    return child; // Return non-button children as is.
  });

  return (
    <div>
      <p>Hey!! You have clicked me 6 time</p>

      <div className='flex items-center gap-2'>
      {modifiedChildren}
      </div>
    </div>
  )
}
