import { useEffect, useState } from "react";
import SearchIcon from "../../Asstets/Icons/SearchIcon";
import TablesRow from "./TablesRow";
import CustomModal from "../../Feature/Modal/CustomModal";
import AddProductForm from "../CommonComponent/CustomForm";
import { addData } from "../../DB/Actions/addProducts";
import { Product, Stores } from "../../DB/db";
import { v4 as uuidv4 } from 'uuid';
import { getStoreData } from "../../DB/Actions/retriveProducts";
// import { MagnifyingGlassIcon, PencilIcon, UserPlusIcon } from "@heroicons/react/24/outline";

const TABS = [
  { label: "All", value: "all" },
//   { label: "Monitored", value: "monitored" },
//   { label: "Unmonitored", value: "unmonitored" },
];


function ProductsTable() {
  const uid = uuidv4();
const [allProducts, setAllProducts] = useState<Product[]>([])
  const [selectedTab, setSelectedTab] = useState("all");
  const [search, setSearch] = useState("");
  const [product, setProduct] = useState({
    id: uid,
    img: '',
    name: '',
    description: '',
    price: '',
  });

  const [isModalOpen, setIsModalOpen] = useState<boolean>(false);
  const openModalhandaler = () => {
      setIsModalOpen((prev) => !prev);
  };




  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setProduct({
      ...product,
      [name]: value,
    });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    addData(Stores.Products, product)
    // console.log('Product Added:', product);
    // Handle form submission, e.g., send to API or update state
  };


useEffect(() => {
  // Define the async function
  const fetchData = async () => {
    try {
      // Await the async data fetching function
      const products = await getStoreData<Product>(Stores.Products);
      setAllProducts(products)
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  // Call the async function
  fetchData();
}, []);
console.log('allProducts---', allProducts)


  return (
    <div>
        <div className="w-full mx-auto my-8 p-4 bg-white shadow-md rounded-lg">
      <div className="mb-4 flex justify-between items-center">
        <div className=" text-left">
          <h2 className="text-xl font-semibold text-blue-gray-700">Customize Your Products</h2>
          <p className="text-sm text-gray-500">See information about all Product</p>
        </div>
        <div className="flex items-center space-x-2">
          <button className="px-4 py-2 text-sm font-semibold border border-gray rounded-md">View All Products</button>
          <button onClick={openModalhandaler} className="flex items-center px-4 py-2 text-sm font-semibold  bg-green text-white rounded-md">
             Add Product
          </button>
        </div>
      </div>
      <div className="flex items-center justify-between mb-4">
        <div className="flex space-x-4 ">
          {TABS.map((tab) => (
            <button
              key={tab.value}
              onClick={() => setSelectedTab(tab.value)}
              className={`py-2 px-4 font-medium ${selectedTab === tab.value ? "border-b-2 border-blue-500 text-blue-600" : "text-gray-500"}`}
            >
              {tab.label}
            </button>
          ))}
        </div>
        <div className="relative flex items-center">
          <input
            type="text"
            placeholder="Search"
            className="w-72 px-4 py-2 border rounded-md"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
          {/* <MagnifyingGlassIcon className="absolute top-3 right-3 h-5 w-5 text-gray-400" />
           */} <div>
            <SearchIcon/>
           </div>
        </div>
      </div>
      <TablesRow/>

      <div className="flex justify-between items-center mt-4">
        <p className="text-sm text-gray-500">Page 1 of 10</p>
        <div className="flex space-x-2">
          <button className="px-3 py-1 text-sm border rounded-md text-gray-600">Previous</button>
          <button className="px-3 py-1 text-sm border rounded-md text-gray-600">Next</button>
        </div>
      </div>
    </div>
    {
        <CustomModal isModalOpen={isModalOpen} openModalhandaler={openModalhandaler} form={<AddProductForm handleSubmit={handleSubmit} product={product}  handleChange={handleChange} />} />
    }
    </div>
  );
}

export default ProductsTable;
