import React from "react";

export default function About() {
  const aboutSections = [
    {
      id: 1,
      title: "Quality Assurance",
      description:
        "Each product goes through rigorous quality checks to ensure it meets our high standards.",
    },
    {
      id: 2,
      title: "Sustainability",
      description:
        "We are committed to sustainable sourcing and eco-friendly packaging for a greener future.",
    },
    {
      id: 3,
      title: "Customer Satisfaction",
      description:
        "Our priority is your satisfaction, with responsive support and a smooth shopping experience.",
    },
    {
      id: 4,
      title: "Fast Shipping",
      description:
        "Get your orders quickly with our reliable and expedited shipping options.",
    },
    {
      id: 5,
      title: "Secure Payments",
      description:
        "Shop with confidence using our secure and convenient payment gateways.",
    },
    {
      id: 6,
      title: "Exclusive Deals",
      description:
        "Enjoy exclusive discounts and promotions only available on our website.",
    },
    {
      id: 7,
      title: "Wide Product Range",
      description:
        "Find everything you need, from electronics to home essentials, all in one place.",
    },
    {
      id: 8,
      title: "Easy Returns",
      description:
        "Not satisfied? We offer hassle-free returns and exchanges to make shopping worry-free.",
    },
    {
      id: 9,
      title: "Gift Options",
      description:
        "Send thoughtful gifts with customizable wrapping options and personalized messages.",
    },
    {
      id: 10,
      title: "Loyalty Rewards",
      description:
        "Earn points on every purchase and redeem them for exciting rewards and discounts.",
    },
    {
      id: 11,
      title: "24/7 Support",
      description:
        "Our support team is available around the clock to assist with any inquiries or issues.",
    },
    {
      id: 12,
      title: "Product Reviews",
      description:
        "Read honest customer reviews to help make informed purchase decisions.",
    },
    {
      id: 13,
      title: "Community Impact",
      description:
        "A portion of our profits supports local communities and charity organizations.",
    },
    {
      id: 14,
      title: "Free Shipping on Orders Over $50",
      description:
        "Enjoy free shipping on all orders over $50 as a token of appreciation.",
    },
    {
      id: 15,
      title: "Latest Trends",
      description:
        "Shop the latest products in tech, fashion, and more, curated for every lifestyle.",
    },
  ];

  return (
    <div className="p-4">
      {aboutSections?.map((item, index) => {
        return (
          <div className="bg-red shadow-md text-left p-2 rounded">
            <h4>{item.title}</h4>
            <p className=" text-sm">{item.description}</p>
          </div>
        );
      })}
    </div>
  );
}
