import React, { useState } from 'react'
import Post from '../../Feature/Post/Post'
import { useQuery } from '@tanstack/react-query';
// import config from '../../config/confix'

const fetchPosts = async () => {
  const response = await fetch('https://jsonplaceholder.typicode.com/posts');
  if (!response.ok) {
    throw new Error('Network response was not ok');
  }
  return response.json();
};

export default function UserPost() {
  const [selectedOption, setSelectedOption] = useState(null);
  console.log("selectedOption--", selectedOption )
  const {data} = useQuery({ queryKey: ['posta'], queryFn: fetchPosts })
  console.log("data---", data)


  const options = data?.map((item:any, index:number) => {
    return {
      label: item.title,
      value: item.id
    }
  })

  const handleSelect = (option: any) => {
    setSelectedOption(option);
    console.log("Selected:", option);
  };


  return (
    <div className='w-[50%]'>
      <Post data={data} selectedOption={selectedOption} options={options} handleSelect={handleSelect} />
    </div>
  )
}
