import { useEffect, useState } from "react";
import SearchIcon from "../../Asstets/Icons/SearchIcon";
import TablesRow from "./TablesRow";
import CustomModal from "../../Feature/Modal/CustomModal";
import AddProductForm from "../CommonComponent/CustomForm";
import { addData } from "../../DB/Actions/addProducts";
import { Product, Stores } from "../../DB/db";
import { v4 as uuidv4 } from 'uuid';
import { getStoreData } from "../../DB/Actions/retriveProducts";
import { deleteProduct } from "../../DB/Actions/deleteProduct";
import { getDataById } from "../../DB/Actions/retriveSignleProduct";

const TABS = [
  { label: "All", value: "all" },
//   { label: "Monitored", value: "monitored" },
//   { label: "Unmonitored", value: "unmonitored" },
];


function ProductsTable() {
  const uid = uuidv4();
const [allProducts, setAllProducts] = useState<Product[]>([])
const [isProductAdded, setIsProductAdded] = useState<boolean>(false)
  const [selectedTab, setSelectedTab] = useState("all");
  const [search, setSearch] = useState("");
  const [selectedId, setSelecedId] = useState<string>('')
//   const [file, setFile] = useState<any>(null);
  const [product, setProduct] = useState({
    id: uid,
    image: '',
    name: '',
    description: '',
    price: '',
  });

  const [updateProduct, setUpdateProduct] = useState({
    id: '',
    image: '',
    name: '',
    description: '',
    price: '',
  });


  const [isModalOpen, setIsModalOpen] = useState<boolean>(false);
  const [isUpdateModalOpen, setIsUpdateModalOpen] = useState<boolean>(false);

  const openModalhandaler = () => {
      setIsModalOpen((prev) => !prev);
  };

  const updateModalHandler = () => {
    setIsUpdateModalOpen((prev) => !prev);
  }



  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setProduct({
      ...product,
      [name]: value,
    });
  };

  const handleChangeUpdatedData = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setUpdateProduct({
      ...product,
      [name]: value,
    });
  }


  const fileToBlob = (file: File): Promise<Blob> => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => resolve(new Blob([reader.result as ArrayBuffer], { type: file.type }));
      reader.onerror = (error) => reject(error);
      reader.readAsArrayBuffer(file);
    });
  };  


  const handleUploadImage = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const selectedFile = e.target.files?.[0];
    const { name } = e.target;
    if (selectedFile) {
      try {
        const blob = await fileToBlob(selectedFile);
        const url:any = URL.createObjectURL(blob); // Create a URL for the Blob
        setProduct({
            ...product,
            [name]: url,
          });
      } catch (error) {
        console.error('Error converting file to blob:', error);
      }
    }
  };


  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    addData(Stores.Products, product)
    setIsProductAdded(true)
  };

  const handleUpdateExistingProduct = () => {

  }

  const handleDeleteProduct = (id:string) => {
    deleteProduct(Stores.Products, id)
    setIsProductAdded(true)
  }

  const handleUpdateProduct = (id: string) => {
    getDataById(Stores.Products, id)
    .then((data: any) => {
      if (data) {
        setUpdateProduct({
            id: data.id,
            image: data.image,
            name: data.name,
            description: data.description,
            price: data.price
        })
        console.log('Retrieved data:', data);
      } else {
        console.log('No data found for the given ID.');
      }
    })
    .catch(error => {
      console.error('Error retrieving data:', error);
    });
    updateModalHandler()
  }




useEffect(() => {
  // Define the async function
  const fetchData = async () => {
    try {
      // Await the async data fetching function
      const products = await getStoreData<Product>(Stores.Products);
      setAllProducts(products)
      setProduct({
        id: uid,
        image: '',
        name: '',
        description: '',
        price: '',

      })
      setIsModalOpen(false)
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  // Call the async function
  fetchData();
}, [isProductAdded]);

setTimeout(() => {
    setIsProductAdded(false)
}, 100)

  return (
    <div>
        <div className="w-full mx-auto my-8 p-4 bg-white shadow-md rounded-lg">
      <div className="mb-4 flex justify-between items-center">
        <div className=" text-left">
          <h2 className="text-xl font-semibold text-blue-gray-700">Customize Your Products</h2>
          <p className="text-sm text-gray-500">See information about all Product</p>
        </div>
        <div className="flex items-center space-x-2">
          <button className="px-4 py-2 text-sm font-semibold border border-gray rounded-md">View All Products</button>
          <button onClick={openModalhandaler} className="flex items-center px-4 py-2 text-sm font-semibold  bg-green text-white rounded-md">
             Add Product
          </button>
        </div>
      </div>
      {/* <img src={blobUrl} alt="Uploaded Preview" /> */}
      <div className="flex items-center justify-between mb-4">
        <div className="flex space-x-4 ">
          {TABS.map((tab) => (
            <button
              key={tab.value}
              onClick={() => setSelectedTab(tab.value)}
              className={`py-2 px-4 font-medium ${selectedTab === tab.value ? "border-b-2 border-blue-500 text-blue-600" : "text-gray-500"}`}
            >
              {tab.label}
            </button>
          ))}
        </div>
        <div className="relative flex items-center">
          <input
            type="text"
            placeholder="Search"
            className="w-72 px-4 py-2 border rounded-md"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
          {/* <MagnifyingGlassIcon className="absolute top-3 right-3 h-5 w-5 text-gray-400" />
           */} <div>
            <SearchIcon/>
           </div>
        </div>
      </div>
      <TablesRow allProducts ={allProducts} handleDeleteProduct={handleDeleteProduct} handleUpdateProduct={handleUpdateProduct} />

      <div className="flex justify-between items-center mt-4">
        <p className="text-sm text-gray-500">Page 1 of 10</p>
        <div className="flex space-x-2">
          <button className="px-3 py-1 text-sm border rounded-md text-gray-600">Previous</button>
          <button className="px-3 py-1 text-sm border rounded-md text-gray-600">Next</button>
        </div>
      </div>
    </div>
    {
        <CustomModal title={'Add New Product'} isModalOpen={isModalOpen} openModalhandaler={openModalhandaler} form={<AddProductForm handleUploadImage={handleUploadImage} handleSubmit={handleSubmit} product={product}  handleChange={handleChange} />} />
    }
      {
        <CustomModal title={'Update Existing Product'} isModalOpen={isUpdateModalOpen} openModalhandaler={updateModalHandler} form={<AddProductForm handleUploadImage={handleUploadImage} handleSubmit={handleUpdateExistingProduct} product={updateProduct}  handleChange={handleChangeUpdatedData} />} />
    }
    </div>
  );
}

export default ProductsTable;
