import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { updateUser } from "../../store/slices/UserSlice";

interface UserInfoProps {
    // id: string,
    name: string,
    email: string
    [key: string]: string;
}

export default function User() {
    const dispatch = useDispatch()
  const filed = [
    {
      name: "Name",
      type: "text",
    },
    {
      name: "Email",
      type: "text",
    },
  ];

  const userInfo:any = useSelector((state) => state)
  const [userInformation, setUserInformation] = useState<UserInfoProps>({
    name: '',
    email: ''

  })

  console.log('userInfo---', userInfo.user)


  const handleInputChange = (e:React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setUserInformation((prevState) => ({
      ...prevState,
      [name]: value, // Dynamically update the corresponding state field based on input name
    }));
  }

//   useEffect(() => {
//     dispatch(updateUser({
//         name: userInformation.name,
//         email: userInformation.email
//     }))
//   }, [dispatch, userInformation])

  return (
    <div className="w-[30%]">
      <form>
        <div className="flex flex-col gap-3">
          {filed.map((item, index) => {
            return (
              <div className="flex flex-col gap-2">
                <p>{item.name}</p>
                <input
                  type={item.type}
                  className="border outline-none rounded  p-2"
                  onChange={handleInputChange}
                  value={userInfo.user[item.name]} // Map the name to the state
                  name={item.name.toLowerCase()} 
                />
              </div>
            );
          })}
        </div>
      </form>
    </div>
  );
}
