          description: "Not satisfied? We offer hassle-free returns and exchanges to make shopping worry-free.",
        },
        {
          id: 9,
          title: "Gift Options",
          description: "Send thoughtful gifts with customizable wrapping options and personalized messages.",
        },
        {
          id: 10,
          title: "Loyalty Rewards",
          description: "Earn points on every purchase and redeem them for exciting rewards and discounts.",
        },
        {
          id: 11,
          title: "24/7 Support",
          description: "Our support team is available around the clock to assist with any inquiries or issues.",
        },
        {
          id: 12,
          title: "Product Reviews",
          description: "Read honest customer reviews to help make informed purchase decisions.",
        },
        {
          id: 13,
          title: "Community Impact",
          description: "A portion of our profits supports local communities and charity organizations.",
        },
        {
          id: 14,
          title: "Free Shipping on Orders Over $50",
          description: "Enjoy free shipping on all orders over $50 as a token of appreciation.",
        },
        {
          id: 15,
          title: "Latest Trends",
          description: "Shop the latest products in tech, fashion, and more, curated for every lifestyle.",
        },
      ];

  return (
    <div className='p-4'>
        {
            aboutSections?.map((item, index) => {
                return (
                <div className='bg-white shadow-md text-left p-2 rounded'>
                        <h4>{item.title}</h4>
                        <p className=' text-sm'>description</p>
                    </div>
                )
            })
        }

    </div>
  )
}
