import { useState } from "react";
import Dropdown from "../../CommonComponent/Button/SelectDropDown/SelectDropdown";

interface Data {
  userId: number;
  id: number;
  title: string;
  body: string;
}

export default function Post({ data }: any) {
  const [selectedOption, setSelectedOption] = useState(null);

  console.log("selectedOption---", selectedOption);

  // const options = [
  //   { label: "Apple", value: "apple" },
  //   { label: "Banana", value: "banana" },
  //   { label: "Cherry", value: "cherry" },
  //   { label: "Date", value: "date" },
  //   { label: "Grape", value: "grape" },
  // ];

  const options = data.map((item:any, index:number) => {
    return {
      label: item.title,
      value: item.id
    }
  })

  const handleSelect = (option: any) => {
    setSelectedOption(option);
    console.log("Selected:", option);
  };

  return (
    <div className="flex flex-col gap-5">
      <div className="flex items-center justify-between">
        <p>Pots</p>

        <div className="w-[300px]">
        <Dropdown options={options} onSelect={handleSelect} />
        </div>
      </div>

      <div className="flex flex-col gap-2">
        {data?.map((item: Data, index: number) => {
          return (
            <div key={index} className="border p-4 flex flex-col gap-3">
              <p className="text-[#1f38f0] font-bold">{item.title}</p>
              <p>{item.body}</p>
            </div>
          );
        })}
      </div>
    </div>
  );
}
