import React from 'react'


// interface Props {
//     TABLE_HEAD?: String[];
// }
export default function TablesRow() {
    const TABLE_HEAD = ["Image", "Title", "Description", "Price", ""];

const TABLE_ROWS = [
    {
      img: "https://demos.creative-tim.com/test/corporate-ui-dashboard/assets/img/team-3.jpg",
      name: "John Michael",
      description: "john@creative-tim.com",
      price: "100 Tk",
    //   org: "Organization",
    //   online: true,
    //   date: "23/04/18",
    },
    {
      img: "https://demos.creative-tim.com/test/corporate-ui-dashboard/assets/img/team-2.jpg",
      name: "Alexa Liras",
      description: "alexa@creative-tim.com",
      price: "100 Tk",
    //   job: "Developer",
    //   org: "Engineering",
    //   online: false,
    //   date: "23/04/18",
    },
    // Add more rows as needed
  ];
  return (
    <div>
      <table className="w-full table-auto text-left border-collapse">
        <thead>
          <tr>
            {TABLE_HEAD.map((head) => (
              <th key={head} className="border-b p-4 text-blue-gray-600 bg-deepGreen border border-light-gray font-medium">
                {head}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {TABLE_ROWS.map(({ img, name, description, price}, index) => (
            <tr key={name} className={" border border-light-gray"}>
                {/* index % 2 === 0 ? "bg-gray-400" :  */}
              <td className="p-4">
                <div className="flex items-center">
                  <img src={img} alt={name} className="w-8 h-8 rounded-full mr-3" />
                  <div>

                    <p className="text-sm text-gray-500">{description}</p>
                  </div>
                </div>
              </td>
              <td className="p-4">
                <p className="font-medium">{price}</p>
                {/* <p className="text-sm text-gray-500">{org}</p> */}
              </td>
              {/* <td className="p-4">
                <span
                  className={`px-2 py-1 text-xs rounded-full ${
                    online ? "bg-green-200 text-green-700" : "bg-gray-200 text-gray-700"
                  }`}
                >
                  {online ? "Online" : "Offline"}
                </span>
              </td> */}
              {/* <td className="p-4 text-gray-500">{date}</td> */}
              <td className="p-4 flex gap-3 items-center justify-center">
                <button className="text-white bg-deepGreen px-2  rounded ">
                   Edit
                </button>
                <button className="text-white bg-gray  px-2  rounded">
                   Update
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  )
}
