import clsx from "clsx";
import { Link, Outlet, useLocation } from "react-router-dom";
const Index = () => {
  const location = useLocation();

  const Navigation = [
    {
      id: "1",
      path: "/",
      name: "Home",
    },
    {
      id: "2",
      path: "/dashboard",
      name: "Dashboard",
    },
    {
      id: "3",
      path: "/component",
      name: "My Componenet",
    },
  ];

  return (
    <div className=" px-32 ">
      <nav className="fixed w-[100%] z-20">
        <ul
          className={clsx(
            "flex  gap-5 px-6 py-3 text-balck border-b-[1px] border-b-light-gray "
          )}
        >
          {Navigation?.map((item, index) => {
            return (
              <li
                key={index}
                className={clsx(
                  "cursor-pointer text-[#055160] hover:text-white",
                  "hover:bg-[#1f38f0]  px-2 rounded",
                  location.pathname === item.path ? 'bg-[#1f38f0] text-white' : ''
                )}
              >
                <Link to={item.path}>{item.name}</Link>
              </li>
            );
          })}

          {/* <li
              className={clsx(
                "cursor-pointer text-[#055160] hover:text-white",
                "hover:bg-[#1f38f0] px-2 rounded"
              )}
            >
              <Link to="/dashboard">Dashboard</Link>
            </li>

            <li
              className={clsx(
                "cursor-pointer text-[#055160] hover:text-white",
                "hover:bg-[#1f38f0] px-2 rounded"
              )}
            >
              <Link to="/component">My Component</Link>
            </li> */}
        </ul>
      </nav>
      <div className="pt-[60px] min-h-[100vh] ">
        <Outlet />
      </div>
    </div>
  );
};

export default Index;
