import clsx from "clsx";
import { Link, Outlet, useLocation } from "react-router-dom";
const Index = () => {
  const location = useLocation();

  const Navigation = [
    {
      id: "1",
      path: "/",
      name: "Home",
      nestedPath: []
    },
    {
      id: "2",
      path: "/dashboard",
      name: "Dashboard",
      nestedPath: [{
        id: "1",
        path: "/post",
        name: "Post",
      },

    ]
    },
    {
      id: "3",
      path: "/component",
      name: "My Componenet",
      nestedPath: []
    },
  ];

  return (
    <div className=" px-32 ">
      <nav className="fixed w-[100%] z-20">
        <ul
          className={clsx(
            "flex  gap-5 px-6 py-3 text-balck border-b-[1px] border-b-light-gray "
          )}
        >
          {Navigation?.map((item, index) => {
            return (
              <li
                key={index}
                className={clsx(
                  "cursor-pointer text-[#055160] hover:text-white",
                  "hover:bg-[#1f38f0]  px-2 rounded",
                  location.pathname === item.path ? 'bg-[#1f38f0] text-white' : ''
                )}
              >
                <Link to={item.path}>{item.name}</Link>
              </li>
            );
          })}

        </ul>

        {/* <ul
          className={clsx(
            "flex  gap-5 px-6 py-3 text-balck "
            // border-b-[1px] border-b-light-gray 
          )}
        >
          {Navigation?.map((item, index) => {
            return (
              <li
                key={index}
                className={clsx(
                  "cursor-pointer text-[#055160] hover:text-white",
                  "hover:bg-[#1f38f0]  px-2 rounded",
                  location.pathname === item.path ? 'bg-[#1f38f0] text-white' : ''
                )}
              >
                <Link to={item.path}>{item.name}</Link>
              </li>
            );
          })}

        </ul> */}
      </nav>
      <div className="pt-[120px] min-h-[100vh] ">
        <Outlet />
      </div>
    </div>
  );
};

export default Index;
