import React, { ReactElement } from 'react'

type MyComponentProps = {
  children: React.ReactNode;
};

export default function MyComponent({children}:MyComponentProps) {
  // const modifiedChildren = React.Children.map(children, (child:any, index) => {
  //   if (React.isValidElement(child)) {
  //     return React.cloneElement(child as ReactElement<any>, { disabled: index !== 0 });
  //   }
  //   return child;
  // });

  const modifiedChildren = React.Children.map(children, (child: any, index) => {
    if (React.isValidElement(child)) {
      // Customize the props for the first button
      if (index === 0) {
        return React.cloneElement(child as ReactElement<any>, {
          onClick: () => alert("First button clicked!"),
          className: `bg-blue-500`, // Add custom styles for the first button
          disabled: false, // Make it clickable
        });
      }
      // Customize the props for other buttons
      return React.cloneElement(child as ReactElement<any>, {
        onClick: undefined, // Remove onClick for other buttons
        className: `bg-gray-400 cursor-not-allowed`, // Add custom styles for disabled buttons
        // disabled: true, // Disable other buttons
      });
    }
    return child;
  });

  return (
    <div>
      <p>Hey!! You have clicked me 6 time</p>

      <div className='flex items-center gap-2' onClick={() => console.log("Click")} >
      {modifiedChildren}
      </div>
    </div>
  )
}
